library(readr)
dat <- read_csv("~/Documents/Work/Projects/tagging/ssimp_software/gwas/small.random.csv")
dat$P <- 2*(1-pnorm(abs(dat$z)))
dat$se <- 1/sqrt(1000) ## not real
dat$b <- dat$z * dat$se
dat$OR <- exp(dat$b)

## dat P, b
## ----------
dat.P <- dat[,c("rnpid", "a1", "a2", "P","b")]

write_csv(dat.P, "~/Documents/Work/Projects/tagging/ssimp_software/gwas/small.random.p.b.csv")


## dat b, se
## -------------
dat.b.se <- dat[,c("rnpid", "a1", "a2", "b","se")]
write_csv(dat.b.se, "~/Documents/Work/Projects/tagging/ssimp_software/gwas/small.random.b.se.csv")


## dat OR, P
## ---------------
dat.OR <- dat[,c("rnpid", "a1", "a2", "P","OR")]
write_csv(dat.OR, "~/Documents/Work/Projects/tagging/ssimp_software/gwas/small.random.p.or.csv")


## dat N
## ---------------
dat.n <- dat[,c("rnpid", "a1", "a2","z")]
set.seed(3)
dat.n$N <- sample(900:1000, nrow(dat.n), replace = TRUE)
write_csv(dat.n, "~/Documents/Work/Projects/tagging/ssimp_software/gwas/small.random.n.csv")



